<template>
<nav class="navbar earthworks-navbar" role="navigation" aria-label="main navigation">
  <div class="navbar-brand">
    <a class="navbar-item" href="/">
      <img height="36" src="data:image/png;base64,">
    </a>

    <a role="button" class="navbar-burger burger" aria-label="menu" aria-expanded="false" data-target="navbarBasicExample">
      <span aria-hidden="true"></span>
      <span aria-hidden="true"></span>
      <span aria-hidden="true"></span>
    </a>
  </div>

  <div id="navbarBasicExample" class="navbar-menu">
    <div class="navbar-start">
      <div class="navbar-item has-dropdown is-hoverable">
        <a class="navbar-link">
          Create
        </a>
        <div class="navbar-dropdown is-boxed">
          <router-link class="navbar-item" :to="{ name: 'new-project' }">Project</router-link>
        </div>
      </div>
    </div>

    <div class="navbar-end">
      <div class="navbar-item has-dropdown is-hoverable">
        <a class="navbar-link">
          {{ username }}
        </a>
        <div class="navbar-dropdown is-boxed">
          <a class="navbar-item" href="#" @click="logout">Logout</a>
        </div>
      </div>
    </div>
  </div>
</nav>

</template>

<script>
export default {
  name: 'Header',
  data () {
    return {
      username: null,
      picture: null,
      authenticated: false
    }
  },
  watch: {
    $auth () {
      this.username = this.$auth.name
      this.picture = this.$auth.picture
    }
  },
  methods: {
    logout () {
      this.$auth.logout()
    }
  },
  created () {
    this.username = this.$auth.authenticated ? this.$auth.name : null
    this.picture = this.$auth.authenticated ? this.$auth.picture : null

    this.$auth.authNotifier.on('authChange', authState => {
      this.authenticated = authState.authenticated
      this.username = authState.authenticated ? this.$auth.name : null
      this.picture = authState.authenticated ? this.$auth.picture : null
    })
  }
}
</script>

